mapper.select:
    select  

   	 from dbo.config_controller WHERE 1=1     
    	  and  id = #{id,jdbcType=INT(11)}  
		  and  group_id = #{groupId,jdbcType=VARCHAR(10)}  
		  and  table_schema = #{tableSchema,jdbcType=VARCHAR(300)}  
		  and  table_name = #{tableName,jdbcType=VARCHAR(150)}  
		  and  table_type = #{tableType,jdbcType=VARCHAR(50)}  
		  and  table_desc = #{tableDesc,jdbcType=VARCHAR(160)}  
		  and  api_value = #{apiValue,jdbcType=VARCHAR(120)}  
		  and  notes = #{notes,jdbcType=VARCHAR(1000)}  
		  and  flag = #{flag,jdbcType=VARCHAR(100)}  
		  and  producers = #{producers,jdbcType=VARCHAR(160)}  
		  and  required = #{required,jdbcType=VARCHAR(11)}  
		  and  isdisabled = #{isdisabled,jdbcType=INT(11)}  
		  and  construct = #{construct,jdbcType=VARCHAR(11)}  
		  and  ignored = #{ignored,jdbcType=INT(11)}  
		  and  request_path = #{requestPath,jdbcType=VARCHAR(110)}  
		  and  request_method = #{requestMethod,jdbcType=VARCHAR(40)}  
		  and  function_name = #{functionName,jdbcType=VARCHAR(110)}  
		  and  query = #{query,jdbcType=VARCHAR(4000)}  
		  and  auth = #{auth,jdbcType=INT(11)}  
		  and  update_date = #{updateDate,jdbcType=TIMESTAMP}
